INSERT INTO `linkedin_data` VALUES ('50498', 'Jane', 'Wang 王姮', '教育系统', 'inspired by the holistic approach in education and life', '中国 北京市区', '[{\"active\":true,\"schoolName\":\"首都经济贸易大学\"}]', '[{\"companyName\":\"Academic Travel Abroad\",\"description\":\"ATA is the mother company of CET. For two years, I was chosen to be a tour manager for their National Geographic China tour, once a year. My duty included quality and expense management, passenger safety and leisure control, emergency management, supervising ATA’s China partners throughout the tour and write report at the end of the tour\",\"title\":\"Tour Manager\",\"rangeDate\":\"2010-10至2011-10\"},{\"locationName\":\"中国 北京市区\",\"companyName\":\"United World College USA\",\"description\":\"UWC USA hosts a very special youth leadership program in the summer since 2011. It brings together 30 students who are from about 10 different nations every year to forge a leadership experience. Students learn: Critical Thinking and Problem-solving, Collaboration and Leading by Influence, Intercultural Awareness, Innovative Approaches to Leadership, Media Literacy, An Edge on College Admissions, An Insider\'s View of America. \\nI have been an independent recruiter for this program in China, and have had full experience of the program as a co-learner. \\nThe experience of this program ignited my passion in youth development.\\nProgram can be found at: www.uwc-usa.org/glf\",\"title\":\"Summer Program Recruiter\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2011-7至2013-7\"},{\"companyName\":\"China Guangcai International Investment Group\",\"description\":\"As the executive assistant of the natural resource department, my main duty was to be the contact person for all our overseas partners, arrange travels and meetings, attend meetings as interpreter, provide meeting notes, join discussions for strategy, travel with director abroad, document translation between Chinese and English.\",\"title\":\"Assistant to Director\",\"rangeDate\":\"2008至2009\"},{\"locationName\":\"中国 北京市区\",\"companyName\":\"FYSE\",\"description\":\"FYSE is a precious organization working with young Chinese social entrepreneurs. \\nFrom end of 2013 to February 2014, while being at home with my newborn, I worked with the organization For Youth Social Entrepreneurs (FYSE) on two projects, a two-day entrepreneurial skill training project with the International Labor Organization where I delivered a curriculum in Chinese language which was originally written by a colleague in English, and a youth social entrepreneurship certificate program with Beijing 101 School where I conducted 6 workshops in a curriculum provided by FYSE. \\nAlso, in 2011, I was selected as one of seven candidates to join FYSE\'s first social entrepreneurial training for women - Spark:Her. During the program, subjects like legal status for grass root social organizations in China, business canvas, marketing and sales strategy, information research and analysis, questionnaire design were introduced and studied on. \",\"title\":\"program associate\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2013-12至2014-2\"},{\"locationName\":\"中国 北京市区\",\"companyName\":\"CET Academic Programs\",\"description\":\"CET is a study abroad company based in Washington D.C. USA. It provides various programs in a few countries for American college students.\\nAt CET, My work included: oversee and provide assistance to student life and studies, organize and plan for activities and excursions, manage program expenses, provide student counseling, emergency assistance and problem solving, recruite and manage Chinese students who roommate with American students\\nWorking with CET has been joyful. The experience interacting with students is invaluable. Study abroad, I believe, can, in a way, change one\'s life.\",\"title\":\"Assistant Director\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2009-10至2012-10\"}]', '[\"Youth Development\",\"International Education\",\"Cross-cultural Training\",\"Youth Leadership\"]', 'janewang', '2017-05-17 16:01:37');
