INSERT INTO `linkedin_data` VALUES ('50501', 'Jason (Xin)', 'Wang', '半导体', 'ASIC Verification Engineer at Quantenna Communications', '中国 上海市区', '[{\"active\":true,\"schoolName\":\"上海交通大学\"}]', '[{\"locationName\":\"中国 上海市区\",\"companyName\":\"Advanced Micro Devices\",\"description\":\"DV architect and technical lead, such as testbench infrastructure, OVM/UVM\\nimplement and UVM transition; PCIe/AXI IP verification, VIP reuse in IP level and SOC level\\n\\nDefine UVM testbench transition stage plan, resource and schedule estimate. Create re-usable testbench framework, include interface UVC, module UVC, system UVC, UVM-RAL, multi-layered sequencer control, stand-in mode, function coverage model, legacy C++ test reuse in UVM, etc.\\n\\n1. DV architect and technical lead for nBIF(Bus interface in PCIe and GFX/Display/SMU/IOC, etc) legacy C++ testbench transition to UVM for next generation SOC. \\n2. DV lead for PCIe-to-AXI bridge, AMD Two-Wire-Interface IP, Graphic Slave module IP, OVM testbench implement and sign off.\\n3. AMD PCIe 2.1/3.0 Root Complex and Endpoint design IP verification\\n4. AMD PCIe 2.1/3.0 Verification IP development and migration from Intel CHSI BFM \\n5. PCIe/BIF/SOC level performance test and gatesim\",\"title\":\"Member of Technical Staff ASIC Verification Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2008-6至2013-9\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"LSI Corporation\",\"description\":\"UVM-MS implementation, analog/mixed-signal(AMS) design verification in Pre-Amplifier team\\n1. Innovate top-down approach to AMS(analog/mixed-signal) design verification\\n2. Implement UVM-MS with emphasis towards AMS Spice/Spectre netlist or behavioral model design verification from scratch; include re-usable UVM-MS testbench creating and constrained random test generation, coverage collection, etc.\\n3. Innovate analog assertion based verification methodology into Spice/Spectre netlist simulation\\n4. Verify both functionality and performance features in AMS IP/SOC level\\n5. Implement metric driven verification for AMS design by Cadence vPlan/vManager\\n\\n Regulator, gain stage, reader top module have been verified by UVM-MS TB.\",\"title\":\"Staff Analog Verification Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2013-9至2014-6\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"S3 Graphics\",\"description\":\"1.Development of function test plan and test case\\n2.Full chip gatesim and sdf back-annotation, timing closure\\n3.Functional ATE test pattern(nandtree, DDR, PCIE, 3D, HDMI, analog macro, etc) generation and verification;\\n4.Pattern format convert from VCD to cycle-based TSTL2;\\n5.Support pattern debug for ATE test; RMA test etc.\",\"title\":\"ASIC Verification Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2005-4至2007-6\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"Trident Microsystems\",\"description\":\"1. Scan chain insertion and scan drc check\\n2. EDT logic generation and compressed pattern ATPG\\n3. At speed test design and verification\\n4. Memory bist design and verification\\n5. JTAG Boundary Scan Test design and Verification\\n6. Scan clock group and balance, STA for scan mode\\n7. Test Pattern generation and Support TE debug chip failure analysis\",\"title\":\"ASIC DFT Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2007-8至2008-5\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"Quantenna Communications\",\"description\":\"IEEE WLAN 802.11 n/ac IP verification and Wifi based SOC verification\",\"title\":\"ASIC Verification Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2014-7\"}]', '[\"DFT\",\"PCIe\",\"AXI\",\"UVM\"]', 'jason-xin-wang-55787a14', '2017-05-17 16:01:38');
