INSERT INTO `linkedin_data` VALUES ('50518', 'Paula', 'Wang', '互联网', 'Director, Online Partnerships Group at Google', '北京 海淀区', '[{\"active\":true,\"schoolName\":\"DePaul University\"},{\"active\":true,\"schoolName\":\"Northwestern University - Kellogg School of Management\"},{\"active\":true,\"schoolName\":\"美国宾夕法尼亚大学 - 沃顿商学院\"}]', '[{\"locationName\":\"San Jose, CA/ Beijing, China\",\"companyName\":\"Cisco Systems\",\"description\":\"Lead the Marketing Platform team (Digital Marketing/Social Media, Event Marketing) for Asia Pacific Japan China (APJC) Theater and the Greater China Region. Launched the flagship event platform Cisco Connect/Live in Greater China. Ran the 100-city roadshow with and for our SMB partners across China.\\n\\nBuild scalable marketing platforms, including digital/inbound marketing/social media marketing (Social Ambassador Program, Online Community, On-demand video marketing), strategic events (Cisco Live, Cisco Connect) and roadshow marketing (Multi-City Roadshow).\\n\\nTransform the field marketing from ad-hoc events to integrated marketing engagement with customers.\\n\\nSuccessfully launched the Cisco Interaction Network Techwise online TV show in China.\\n\\nLed customer acquisition team for Cisco\'s 2010 WorldExpo marketing. Drove customer engagement for Smart Connected Communities for a pipeline of $2Billion.\",\"title\":\"Head of Digital/Social Marketing & Field Marketing\",\"rangeDate\":\"2009-6至2014-4\"},{\"locationName\":\"中国 北京市区\",\"companyName\":\"Google\",\"description\":\"Lead the fastest growing region for Google\'s WW OPG buisness. Run a $700million business unit for Google Greater China (incl. China, Taiwan and HongKong). We strive to help millions of mobile App developers and publishers to monetize globally.\\n\\nChampion the One Google solution to provide the best experience and serivces to our develoeprs and publishers.\",\"title\":\"Director, Online Partnerships Group, Greater China\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2016-9\"},{\"locationName\":\"中国 北京市区\",\"companyName\":\"Microsoft\",\"description\":\"Lead the overall Microsoft China partner ecosystem transformation strategy including Cloud partner recruitment, enablement, partner incentives, and marketing. Manage a team of senior sales and marketing professionals to drive the overall partner-led revenue contribution to the company\'s topline in China.. Drive the overall community engagement with a variety of partners, including ISVs/CSPs (Cloud Solution Partners). \\n•\\tBuilt the partner cloud capacity for Microsoft China from an ecosystem of a few to over 2,500 for O365 and Azure. \\n•\\tCreated the ISV Hub (a Marketplace-like community) to drive ISV recruitment, Partner-to-Partner connection and ISV engagement with Microsoft.\\n•\\tTransformed how Microsoft goes to market in China, from MSFT-led Anti-Piracy motion to Partner-Led solution-selling motion. Improved the co-selling revenue contribution from 7% in FY15 to 18% in FY16.\\n•\\tEstablished the Microsoft Partner Business School, with MSFT sellers train Partner sellers, MSFT marketers train Partner marketers and MSFT technical engineers train Partner engineers and integrated training with marketing investment and partners sales\\n•\\tImproved partner marketing capabilities and ROI from 1:15 to 1:50. Significantly improved the partner attach to Cloud deals.\\n\",\"title\":\"Sr. Director, Cloud Partner Development Strategy and Marketing\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2014-5至2016-9\"},{\"locationName\":\"San Diego, CA\",\"companyName\":\"Sony Electronics Inc.\",\"description\":\"Drive online marketing and sales for the Personal Audio and Mobile Electronics categories including MP3 players and personal navigation systems.\\n\\nSpearhead a renewed focus on optimizing online engagement with customers and ultimately driving maximum sales online both via sonystyle.com, and select online merchants such as amazon.com, bestbuy