INSERT INTO `linkedin_data` VALUES ('50627', 'Huai (Harry)', 'Wang', '投资管理', 'Founding Partner at Linear Venture (linear.vc)', '中国 上海市区', '[{\"active\":true,\"schoolName\":\"浙江大学\"},{\"active\":true,\"schoolName\":\"美国俄亥俄州立大学\"},{\"active\":true,\"schoolName\":\"美国斯坦福大学\"}]', '[{\"locationName\":\"中国 上海市区\",\"companyName\":\"Zhijing Capital\",\"description\":\"AS an investment company: \\nWe invest in the angel to pre-A range, typically 200K-500K USD equivalent. We emphasize our expertise on the technical, business operations, and industrial connections. \\n\\nPERSONALLY:\\nI am looking forward to meeting the best people who are excited about building great products for real problems. We may be able to work out something together. Even if not, we can learn something from each other.\\n\\nMy angel style is elaborated in blog.sina.com.cn/s/blog_70c9335b010135xg.html. Please double check before you reach out for help :)\",\"title\":\"Partner\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2012-7\"},{\"locationName\":\"Shanghai, Hong Kong\",\"companyName\":\"Linear Venture\",\"description\":\"Linear Venture is a 100M USD fund. \\n\\nThe phases we cover are angel round to series A round, where we can lead or co-invest 0.5M to 3M USD in each deal.\\n\\nI am responsible for sourcing and investing into venture fundable technology driven China startups that Linear Venture is determined to help.\\n\\nOur team has invested into 40+ companies so far including dianping.com, baixing.com, kujiale.com, aylanetworks.com (US), nashangban.com, dianrong.com, shequ001.com, lvmama mobile, juesheng.com, fraudmetrix.cn, vulbox.com and etc.\",\"title\":\"Founding Partner\",\"rangeDate\":\"2014-9\"},{\"locationName\":\"Shanghai, China\",\"companyName\":\"Baixing.com\",\"description\":\"I spent 6 weeks full-time at Baixing to help on product, technology and team building, and now kinda on call whenever Baixin needs my hlep.\",\"title\":\"CEO Advisor\",\"rangeDate\":\"2012-4\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"大众点评Dianping.com\",\"description\":\"I advise on challenging engineering and product issues faced by dianping.com. To be precise, I am spending my Tuesday and Thursday afternoons working with the team on day to day challenges.\",\"title\":\"CEO Advisor\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2012-10\"},{\"companyName\":\"Facebook\",\"description\":\"I manage the engineering team that fights against payment fraud around Ads and Facebook Credits. The war was tougher than you might think, in that the line b/t good and bad was blurred.\",\"title\":\"Head of Risk Engineering Team\",\"rangeDate\":\"2010-6至2011-9\"}]', '[\"FACTS\",\"Cloud Computing\",\"People Management\",\"LAMP\"]', 'hwang123', '2017-05-17 16:03:20');
