INSERT INTO `linkedin_data` VALUES ('50693', 'Su', 'Wang', '半导体', 'Senior Director of Corporate FP&A & WW BU Finance Controller', '美国 旧金山湾区', '[{\"active\":true,\"schoolName\":\"加拿大渥太华大学\"},{\"active\":true,\"schoolName\":\"深圳大学\"}]', '[{\"locationName\":\"San Francisco Bay Area\",\"companyName\":\"A10 Networks\",\"title\":\"Sr. Director of Finance, FP&A\",\"region\":\"urn:li:fs_region:(us,84)\",\"rangeDate\":\"2016-8至2017-1\"},{\"locationName\":\"美国 旧金山湾区\",\"companyName\":\"Audience, Inc.\",\"description\":\"*Led all aspects of financial planning, forecasting &analysis in support of the continuous growth from private to public with a successful IPO, and from a US-based to a global business \\n* Managed and built the Company’s full FP&A function from a one-man shop to a 3-member highly efficient team, as the go-to financial partners for all functions at all levels, and a strong liaison with the Accounting Teams, Corporate and international entities\\n*Managed end-to-end Sales forecasting process, sales project pipeline tracking, and distribution ops, contracts review in support of WW Sales function\\n*Finance leadership in inventory supply demand review in support of wafer buy decision \\n*Managed OPEX & CAPEX by approving purchase requisitions, owner of month end accrual\\n*Managed workforce planning process, finance approver of HR’s new job requisitions in Jobvite\\n*Responsible for all internal management reporting, including BoD package\\n* Participated in international tax structure and accounting setup and provide reports to support quarterly ETR provision calculation & VA analysis\\n*Key contributor to Audit Committee package, 10-Q, 10-K reviews, auditors’ PBC preparation\\n* Continuously spearheaded various business processes improvement with cross-functional teams\\n*Owned and implemented full functionality of Hyperion Planning & Essbase 11.1.2.3 on time on budget; with the best system and FP&A team, ready to support a $0.5B-$1B revenue size company\\n*Imp