INSERT INTO `linkedin_data` VALUES ('50723', 'Xuhao', 'Wang, Ph.D., EIT', '土木工程', 'Project Manager at National Concrete Pavement Technology Center, Institute for Transportation', 'Ames, Iowa', '[{\"active\":true,\"schoolName\":\"Iowa State University\"}]', '[{\"companyName\":\"Iowa State University\",\"description\":\"Served as teaching assistant and grader for Advanced Design of Pavement Materials and Design of Concrete undergraduate and post graduate courses\",\"title\":\"Teaching Assistant\",\"rangeDate\":\"2011-8至2014-5\"},{\"locationName\":\"Ames, IA\",\"companyName\":\"National Concrete Pavement Technology Center\",\"description\":\"•\\tGain interdisciplinary R&D activities in infrastructure engineering with well-focused projects tailored to address the needs of local, state, and federal agencies, and the private sector\\n•\\tSupervise graduate students in the vast field of transportation infrastructure\\n•\\tAssist center in preparing major infrastructure proposals and present at professional society meetings \\n•\\tPromote technology transfer to the engineering community and industry\",\"title\":\"Postdoctoral Research Associate\",\"rangeDate\":\"2014-5至2015-12\"},{\"companyName\":\"Concrete Pavement Technology Center Laboratory\",\"description\":\"• Trained more than eight graduate/undergraduate students and visiting scholars on laboratory testing\\t \\n• Administered day to day lab operation (e.g., equipment maintenance and materials supply)\\n• Managed more than 16 field demonstrations of DOTs and commercial construction projects\",\"title\":\"Lab Technician\",\"rangeDate\":\"2013-1至2014-5\"},{\"locationName\":\"Ames, Iowa\",\"companyName\":\"Iowa State University\",\"description\":\"•\\tApplied NDE analysis \\no\\tSucceeded in application of ultrasonic P-wave velocity on paving concrete and self-consolidating concrete to provide guidelines for saw cutting group of contractors with an efficient manner\\no\\tDeveloped algorithm on digital image analysis on particle packing to produce cost-efficient SCC\\n•\\tAssessed the application of waste glasses and five supplementary cementitious materials (SCMs) on mitigating alkali silica reaction with statistical models development\\n•\\tAssessed the heat energy transfer on both cement and asphalt concrete pavements in seven states of United States via albedo and heat flux sensor measurements to enhance pavement design database through MEPDG modeling\\n•\\tComputationally modeled shrinkage and fracture behavior of ternary mix concrete and SCC to improve the sustainability of concrete structures \\n•\\tConducted eight-state ternary mixing concrete field demonstrations and provided construction guidelines of ternary mixing concrete applications for DOTs \\n•\\tPerformed on strength, fracture, and fatigue of pervious concrete study and journal paper development\\n•\\tInvestigated 46 SCC mixtures used for cast-in-place bridge structure and developed guidelines and mix proportions for SCC used in AASHTO LRFD Bridge Design and Construction Specifications\\n•\\tSucceeded in applying research outputs to the field demonstration of first internal curing three-span bridge structure in the state of Iowa\\n•\\tStudied integral waterproofing and shrinkage reducing admixtures applications to diversify concrete patch applications \\n•\\tServed as a teaching assistant for undergraduate and graduate courses: Advanced Portland Cement Concretes and Advanced Design of Concretes\",\"title\":\"Research Assistant\",\"honors\":[\"urn:li:fs_honor:(ACoAAAhunnkBEv8KTaho4E9IeJzyq7iHU3pkBIo,55)\"],\"rangeDate\":\"2009-8至2014-5\"},{\"locationName\":\"Iowa State University\",\"companyName\":\"National Concrete Pavement Technology Center, Institute for Transportation\",\"title\":\"Project Manager\",\"rangeDate\":\"2016-1\"}]', '[\"Physics\",\"Statistics minor\",\"Matlab\",\"QA/QC\"]', 'xuhaowang', '2017-05-17 16:05:25');
