INSERT INTO `linkedin_data` VALUES ('50767', 'Tianhan', 'Zhang', '互联网', 'Director of Strategy', '上海 徐汇区', '[{\"active\":true,\"schoolName\":\"University of Nebraska-Lincoln\"}]', '[{\"locationName\":\"中国 上海市区\",\"companyName\":\"聚信立\",\"description\":\" 1. Responsible for developing insightful strategies (short-term & long-term).\\n 2. Provide internal consultant.\\n 3. Provide market, competitive insight and foresight for decision support on strategic growth initiative. \\n \\n\\n 聚信立是一家互联网消费者信用评估平台，我们致力于通过互联网数据来解决存在于B端和C端之间的信息不对称问题。在这个阶段我们专注在现金，消费以及小微商户的贷款领域，每天我们为超过1.5亿人民币贷款提供基于互联网数据的风险控制及信用服务。\\n 聚信立团队由来自于全球知名金融机构的数据科学家以及互联网公司优秀产品和开发人才所组成。\\n 除了聚信立的数据收集产品外，我们也为我们的客户提供一系列的数据分析和交易撮合服务，包括建模，评分卡开发，资产证券化, 资产定价等服务。\\n 在未来，我们的愿景是利用消费者自己在互联网上的数据，来快速证明和提高自己的资信状况，从而帮助他们改善生活的方方面面。\\n \",\"title\":\"Director of Strategy\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2016-2\"},{\"locationName\":\"湖南长沙\",\"companyName\":\"University of technology in Changsha\",\"description\":\"Teaching brief introduction to the History of Western Philosophy\",\"title\":\"Lecturer\",\"rangeDate\":\"2014-8至2016-1\"},{\"locationName\":\"中国 湖南 长沙\",\"companyName\":\"湖南电子信息产业集团\",\"title\":\"市场研究经理\",\"region\":\"urn:li:fs_region:(cn,8985)\",\"rangeDate\":\"2012-6至2014-5\"}]', '[\"Microsoft Excel\",\"Microsoft Office\",\"管理人员\",\"领导力\"]', 'tianhan-zhang-305a5b115', '2017-05-17 16:06:07');
