INSERT INTO `linkedin_data` VALUES ('50815', 'Li', 'Zhang', '制药', 'Staff Bioinformatics Scientists', '美国 旧金山湾区', '[{\"active\":true,\"schoolName\":\"南开大学\"},{\"active\":true,\"schoolName\":\"美国加州大学尔湾分校\"}]', '[{\"companyName\":\"Roche Pharmaceuticals\",\"description\":\"IT Lead Analyst for Application Development at pREDi (Pharma Research and Early Development Informatics)\\n\\n\\n* Lead Analyst with the Agile Prototyping Software Development service strategy team.\\n\\n* Regularly Interact with researchers and/or business analysts to translate and prioritize customer requirements into requirements documents and product development strategies, iteratively build applications to meet evolving scientific needs using scrum and agile development methodology;gather, analyze and synthesize feedbacks from customers.\\n \\n* Developing, supporting, refining and extending customer-facing software and web-based applications for data integration, storage, manipulation and visualization. \\n\\n* Provide in-house and commercial research informatics software development support to the Bioinformatics/Cheminformatics Services groups. Automate bioinformatics and data integration and analysis pipeline\\n\\n* Work with diversity of genomic data, genetic variation data, next generation sequencing, RNA/transcriptome, metabolic, microarrays gene expression, molecule structure,imaging and omics data.\\n\\n• Agility support: Conduct projects following Agile software development methodology using Extreme Programming(XP) and Scrum.\",\"title\":\"IT Lead Analyst\",\"rangeDate\":\"2009-3至2011-8\"},{\"locationName\":\"Menlo Park, California\",\"companyName\":\"AccuraGen\",\"description\":\"Lead the Bioinformatics Data Analysis and Software Development R&D effort in developing in-house LDT, Kit & IVD products for detecting cancer mutations in miniscule concentrations of circulating tumor DNA (ctDNA) in blood, via NGS based proprietary high sensitive targeted method. \",\"title\":\"Staff Bioinformatics Scientist\",\"rangeDate\":\"2016-2\"},{\"locationName\":\"South San Francisco\",\"companyName\":\"Fluidigm Corporation\",\"description\":\"•Developed and continuously enhanced the PCR Assay Design Pipeline for Fluidigm in-house Targeted-Enrichment Sample Prep System (Access Array™), which enables high-throughput amplicon-based library/sample prep for all major Next-Generation Sequencing platforms. \\n\\n•Designed and optimized PCR Assays for R&D new product development for targeted sequencing sample prep system. Also work closely with Assay Design Group (commercial team) to assist external customer assay design work.\\n\\n•Developed novel software algorithm to enable PCR Primers High Multiplexing strategy in order to cover more genomic targets compare to previous version of product offering. \\n\\n•Developed in-house next generation sequencing analysis method and pipeline, perform NGS data analysis for Sample prep performance evaluation against various defined matrices using Illumina platform such as MiSeq and HiSeq. The analysis includes but not limited to sequence alignment, error rate measurement, variant call and annotation, etc. \\n\\n•Developed novel automated Dual-Amplicons Assay Design Pipeline, in conjunction with Genotyping-by-Sequencing (GBS) method for Targeted Cancer Panel development. The solution aims for designing high-accuracy clinical sequencing assays for clinical diagnostics cancer panel development and downstream variant detection applications. \\n\\n•Developed, Optimized and maintained Fluidigm’s SNP Genotyping (GT) Assay Design Software and the design web service for various high throughput genotyping platforms (Juno, BioMark, EP1, BioMark). Conduct GT PCR assay design, evaluation, and optimization \\n\\n•Perform day-to-day Bioinformatics support for internal projects, R&D scientists and external customers (Implement prototype software solutions, guide experiment designs, analyzing and interpret data, troubleshooting, automate QC procedu