INSERT INTO `linkedin_data` VALUES ('50864', 'Leo', 'Zhang', '金融服务', 'Senior Manager at eBay', '中国 上海市区', '[{\"active\":true,\"schoolName\":\"上海财经大学\"}]', '[{\"locationName\":\"Shanghai\",\"companyName\":\"eBay\",\"description\":\"*Manage APAC top rated merchant promotion program, including program implement, monthly intensive review, and covered 40% of APAC revenue.\\n*Evaluate merchant by seller standard and bad buyer experience, take enforcement to punish bad buyer experience driver.\\n*Leverage partner level management process including enter and out to control the overall portfolio healthiness and risk.\\n*Produce weekly APAC portfolio metrics and monthly scorecard for performance tracking & management review\\n*Educate Top-rated seller and account manager with latest policy and risk requirements.\",\"title\":\"Senior Risk Analyst\",\"honors\":[\"urn:li:fs_honor:(ACoAAADaVtIBx4tiTCrqt7oze69TLU7ZdT0IU60,433079964)\"],\"rangeDate\":\"2010-12至2011-7\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"eBay Inc\",\"title\":\"Senior Manager\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2015-2\"},{\"companyName\":\"GMAC SAIC\",\"description\":\"*Design tailored application scorecard & underwriting strategy for the largest auto loan portfolio in China market, by using which, revenue could be doubled with same level of risk factor.\\n*Conduct loan loss forecast and work with global team on the final portfolio reserve planning.\\n*Analysis the current operation process and propose optimizing process with business requirements for loan system.\\n*Take part in regular underwriting process auditing.\\n*Train three junior team staffs.\",\"title\":\"Senior Credit Risk Analyst\",\"rangeDate\":\"2010-3至2010-8\"},{\"locationName\":\"Shanghai\",\"companyName\":\"eBay Inc\",\"description\":\"*Cooperate with banking & insurance partner to provide financial solutions for trusted SME seller with good performance.\\n*Work with Account manager, Marketing, Finance, CS team to effectively & smoothly implement seller growth strategy.\\n*Identify the High-potential SME seller with lower risk and higher growth, design tailored strategies to accelerate growth, reduce risk and improve seller experience. \\n*Taken over the Top Seller management program from an eight people team in one month. Identified solutions for key problems and implement in 2 months after transition, driving the result to the expected direction. \\n*Launch horizontal limit utilization management program for all seller segment of GC, reduced risk level of overall portfolio and gained more than 40MM benefits in second half of 2013, saved more than 30% underwriting CS agent resource. \\n*Enhance risk management approach by providing business intelligence on listings/SKU level for seller to self-manage risk. \\n*Support mobile channel business development and track the risk & business performance ensure the risk under control.\\n*Conduct risk based seller rating adjustment & performance tracking. Visit seller and coach on the performance improvement.\\n*Provide risk initiatives training sessions to account manager, CS agent & sellers across GC region.\\n*Solve urgent bugs and make it right on credit for seller who had experienced natural disaster.\\n*Hire new team members and train new team staffs.\",\"title\":\"Risk Manager\",\"honors\":[\"urn:li:fs_honor:(ACoAAADaVtIBx4tiTCrqt7oze69TLU7ZdT0IU60,433358124)\",\"urn:li:fs_honor:(ACoAAADaVtIBx4tiTCrqt7oze69TLU7ZdT0IU60,433403328)\"],\"rangeDate\":\"2013-4至2015-2\"},{\"locationName\":\"Shanghai\",\"companyName\":\"eBay\",\"description\":\"*Build & implement the APAC tailored seller rating strategy, reduced risk of platform and earned additional 35MM.\\n*Launch brand new seller standard rating program, design & implement the education strategies to conquer challenge and eventually improve the adoption from 1% to 33% in half year, exceeded target by 17%. \\n*Leverage search result tool to control risk of whole APAC region, reduced 30bps bad buyer experience in two months.\\n*Research seller behavior and prevent system gamer and potential fraud through validate tracking and feedback investigation. \\n*Conduct risk based seller rating adjustment & performance tracking. Visit seller and coach on the performance improvement.\\n*Support mobile channel business development and track the risk & business performance ensure the risk under control.\\n*Provide risk initiatives training sessions to account manager, CS agent & sellers across APAC region.\\n*Solve urgent bugs and make it right on credit for seller who had experienced natural disaster.\\n*Hire new team members and train new team staffs.\",\"title\":\"Assistant Risk Manager\",\"honors\":[\"urn:li:fs_honor:(ACoAAADaVtIBx4tiTCrqt7oze69TLU7ZdT0IU60,433177848)\",\"urn:li:fs_honor:(ACoAAADaVtIBx4tiTCrqt7oze69TLU7ZdT0IU60,433261271)\"],\"rangeDate\":\"2011-7至2013-4\"}]', '[\"Analytics\",\"Segmentation\",\"Credit Risk\",\"Risk\"]', 'leocooler', '2017-05-17 16:11:12');
