INSERT INTO `linkedin_data` VALUES ('50885', 'Rui-Yang', 'Zhang', '生物技术', 'Graduate Student at University of Hawaii at Manoa', 'Honolulu, Hawaii', '[{\"active\":true,\"schoolName\":\"阳明大学\"},{\"active\":true,\"schoolName\":\"University of Hawaii at Manoa\"},{\"active\":true,\"schoolName\":\"大同大学\"}]', '[{\"locationName\":\"Medical Departments\",\"companyName\":\"National Taiwan University Hospital\",\"description\":\"The main responsible for using cells culture, western blotting, PCR, real-time PCR, ELISA, Immunohistochemistry, confocal microscope and microarray analysis to investigate the ferritin heavy chain induced regulation of heme oxygenase (decycling)-1 in response to oxidative stress. The other part was the construction of animal models in glomerulonephritis Peritonitis and bacterial Peritonitis to type macrophage between different kinds of inflammations by flow cytometry and microarray analysis.\",\"title\":\"Research Assistant\",\"rangeDate\":\"2012-2至2013-8\"},{\"locationName\":\"Department of Molecular Bioscience and Bioengineering\",\"companyName\":\"University of Hawaii at Manoa\",\"description\":\"Currently main duty is helping with various projects related to synthesis of Conopeptides. Tasks included chemical synthesis of peptides using Boc and Fmoc chemistry, and purified peptides using HPLC and Mass Spectrometer.\",\"title\":\"Graduate Assistant\",\"honors\":[\"urn:li:fs_honor:(ACoAABWqYAwBsmmeJxmvKK3m7rgaxJ_ttSbshc8,686816716)\"],\"rangeDate\":\"2014-1\"},{\"locationName\":\"Department of Biotechnology and Laboratory Science in Medicine\",\"companyName\":\"National Yang Ming University\",\"description\":\"The objectives of graduate works were using homologous recombination technique to \\nselect ceftriaxone-resistance related gene stm1530 deletion mutant in Salmonella enterica serovar Typhimurium to prove that stm1530 plays a key role in ceftriaxone-resistance through antibiotic susceptibility assay, biological function test, transcriptome profile