INSERT INTO `linkedin_data` VALUES ('50895', 'Jian', 'Zhang', '保险', '信息安全总监 - 泰康保险集团', '北京 昌平区', '[{\"active\":true,\"schoolName\":\"浙江大学\"}]', '[{\"locationName\":\"中国 北京市区\",\"projects\":[\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786891119)\"],\"companyName\":\"瞬联软件科技\",\"description\":\"瞬联软件是一家从事软件外包服务的外资公司, 其客户都是欧美高科技公司；它在美国，北京，杭州和成都设有分支办公室。\\n负责计划，组织, 实施 ISO 27001(信息安全国际标准)认证项目；建设公司信息安全管理体系, 建立, 实施, 运行, 监控, 审核, 维护和改进公司信息安全; 制定公司信息安全的政策，流程和指南，部署和维护IT安全系统以改进信息安全，监控信息安全政策的执行，和提供有关信息安全的培训. 公司已经获得ISO27001认证.\\n负责和实施信息安全风险管理: 识别并评估关键的信息资产, 识别威胁和弱点, 评估风险,编制风险评估报告, 推荐、评估并确定控制目标和控制，编制风险处理计划.\\n按照风险处理计划和ISO27001标准，负责设计，实施和维护公司信息技术基础设施)的安全防护体系, 对IT基础设施和应用系统实施安全加固和加强, 制定和实施相关技术措施来规避或消减风险.\\n负责应用系统风险评估、测试和加固\\n负责安全事件监控、应急响应\",\"title\":\"IT经理\",\"region\":\"urn:li:fs_region:(cn,8911)\",\"rangeDate\":\"2003-8至2008-10\"},{\"locationName\":\"北京\",\"projects\":[\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786540980)\",\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786626405)\",\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786692605)\",\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786741648)\",\"urn:li:fs_project:(ACoAAA18UfkBmgkMvZN_rrNTV89-l_TQ3q3LKRc,786817633)\"],\"companyName\":\"完美世界公司\",\"description\":\"完美世界公司是中国领先的网络游戏开发商，运营商和互联网公司，是美国纳斯达克上市公司；开发运营诛仙，DOTA2，完美世界等知名游戏，以及178，电玩巴士，老虎网等知名游戏网站；在北京，上海，成都，台湾，北美和欧洲设有分支机构，有4000多员工。\\n负责制定公司信息安全体系整体规划, 计划，和执行\\n安全团队的组建,管理指导和提升\\n负责公司游戏运维的安全，包含安全平台架构设计、实施，应急响应、日常安全运维管理\\n负责公司web站点安全，包括安全架构设计、实施，安全测试评估、应急响应工作\\n设计和实施公司研发内网的隔离和物理安全, 很好地保障了公司游戏和网站研发(源代码)的安全\\n负责公司信息安全体系(ISMS)建设工作，包括风险评估、体系规划、制度流程制定和落地、培训教育\\n负责开展SOX404 ITGC审计，等级保护等审计的合规建设\\n负责公司内部系统上线前安全测试、架构评估工作, 定期安全测试及加固\\n负责测试评估各类安全系统，进行需求调研分析、测试评估、实施\\n对安全漏洞、事件进行跟踪、负责事件通报\\n安全技术研究评估\\n规划, 设计和建设公司统一单点登录系统(SSO), 集成了公司人事,财务,流程,门户, 研发运营相关的各类业务系统40多个\\n负责系统容灾调研、建设工作\",\"title\":\"信息安全总监\",\"rangeDate\":\"2009-3至2015-4\"},{\"locationName\":\"北京\",\"companyName\":\"中软总公司\",\"description\":\"中软总公司是一家大型国有上市公司。\\n负责公司IT系统的设计和日常管理。\\n担任中国首家Sun 授权Java中心（与Sun公司合作）的主任；负责AJC环境的设计，部署和系统管理，这个AJC包括一台Sun E5000 server, 2台Ultra 2 和20台SPARC。\",\"title\":\"高级系统管理员\",\"rangeDate\":\"1995-7至1999-9\"},{\"locationName\":\"北京\",\"companyName\":\"西康电讯\",\"description\":\"西康电讯是一家美国上市公司；它从事ISP和宽带接入业务；它在北京，上海和深圳设有分支机构。\\n制定公司信息安全政策, 安全手册和相关指南.\\n根据业务和安全需求, 对运营IT基础设施进行定期安全测试评估, 加固.\\n负责安全事件监控、应急响应\\n负责ISP运营IT基础设施的设计，实施和日常管理，包括数据通信，网络，服务器，计费系统和CRM系统。\",\"title\":\"IT Manager\",\"rangeDate\":\"1999-10至2003-8\"},{\"locationName\":\"中国 北京郊区\",\"companyName\":\"泰康保险集团\",\"description\":\"负责泰康集团的信息安全。\",\"title\":\"信息安全总监\",\"region\":\"urn:li:fs_region:(cn,8905)\",\"rangeDate\":\"2015-7\"}]', '[\"ISO27001\",\"Information Security\",\"Linux\",\"SOX\"]', 'jianzhang71', '2017-05-17 16:13:45');
INSERT INTO `linkedin_data` VALUES ('50896', 'Jun', 'Zhang', '电器和电子制造', 'Application Engineer at Keysight Technologies', '上海 浦东新区', '[{\"active\":true,\"schoolName\":\"南京航空航天大学\"}]', '[{\"locationName\":\"中国 上海市区\",\"companyName\":\"Teradyne\",\"description\":\"Main responsibilities:\\n1.Write technology notes to help customers better understand our products;\\n2.Write competitor report to help sales better understand sale points;\\n3.work with service manager to sell service contract;\\n4.Suggest R&D to develop product roadmap to better server domestic customers;\\n5.Act as speaker on seminar and user annual meeting.\\n\\nAchievements:\\n1.Get $3.4M service contract in FY12;\\n2.Get $3.2M service contract in FY11.\",\"title\":\"Service Sales Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"rangeDate\":\"2010-9至2012-11\"},{\"locationName\":\"Shanghai City, China\",\"companyName\":\"Keysight Technologies\",\"description\":\"Job Responsibilities:\\n1.Manage the developments/operations side of large scope projects from proto builds to ramp and serve as technical lead in resolving complex issues during bring-up and life of product.\\n2.Be a technical lead in communicating project readiness. Identify, prioritize and manage key technical changes and risks of a given product; ensure all technical risks associated with a product are identified and closed or mitigated to enable the quality ramp of a product.\\n3.Review all technical requirements, along with an assessment of the robustness of the solution to meet the requirements of the operation, and the implementation of any additional solutions to mitigate any risks to the plan.\\n4.Serve as the senior technical lead for the product line for the beginning to end life cycle of the product. Ensure operations technical readiness for all phases.\\n5.Work with R&D teams and customers to develop test coverage and test plan for new products. Develop test stations with cross-functional teams to ensure manufacturing related defects are detected as many as possible.\\n6.Work with and manage suppliers to develop test fixtures to ensure correct performance and quality per design requirements.\\n7.Good experience in JTAG, Boundary Scan, Functional Test, Embedded System Test, C Language, Python.\\n\\nAchievements:\\n1.Get $4.5M orders in FY16(Apple laptop product).\\n2.Get $3.3M orders in FY14(Apple laptop product).\\n3.Award “FY16 Best Support AE”.\\n4.Award \\\"FY14 Best Sales Team\\\".\\n5.The first time deploy Keysight boundary scan analyzer solution in mass production (daily volume 25000pcs) and achieve 99% FPY.\\n6.Accumulate much project management experience during line setup, people training, process improvement and customer communication.\",\"title\":\"Application Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"honors\":[\"urn:li:fs_honor:(ACoAAAQV_tQBUGcCwmUubb4U_bk_s_DUGBw9Bxc,981692642)\",\"urn:li:fs_honor:(ACoAAAQV_tQBUGcCwmUubb4U_bk_s_DUGBw9Bxc,618370635)\"],\"rangeDate\":\"2014-8\"},{\"locationName\":\"中国 上海市区\",\"companyName\":\"Agilent Technologies\",\"description\":\"Job Responsibilities:\\n1. Gather, translate and prioritize customer needs and technical requirements into technical specifications for ICT products throughout product development;\\n2. Validate product technical design meets target customer requirements; \\n3. Organize product testing at customer sites; \\n4. Provide technical support at customer sites to facilitate adoption of our products;\\n5. Develop technical customer training materials and deliver training;\\n6. Develop white papers, application notes and case studies to facilitate field and customer awareness; \\n7. Provide support to field during evaluations and benchmarking activities to drive adoption and sales; \\n8. Identify technical trends and develop applications and collaterals (presentations, publications, literature etc) to drive business development.\\n\\nJob Achievements:\\n1.Help company to break into boundary scan analyzer market as new player.\\n2.Get $0.5M orders(Apple laptop product).\",\"title\":\"Technical Marketing Engineer\",\"region\":\"urn:li:fs_region:(cn,8909)\",\"honors\":[\"urn:li:fs_honor:(ACoAAAQV_tQBUGcCwmUubb4U_bk_s_DUGBw9Bxc,618370636)\"],\"rangeDate\":\"2012-12至2014-7\"},{\"locationName\":\"上海\",\"companyName\":\"安捷伦科技（中国）有限公司\",\"description\":\"主要职责：\\n收集、翻译客户技术需求并反馈给产品研发部门；\\n验证产品设计是否满足客户需要；\\n客户有新产品导入时提供技术支持；\\n为客户提供现场技术支持以赢得客户对本司产品、技术和服务的好感；\\n撰写培训资料并给客户作培训；\\n撰写白皮书，技术文档和学习案例以帮助客户了解本司产品和技术优势； \\n在与竞争对手进行现场产品评估时提供技术支持以帮助赢得订单； \\n判断行业发展趋势并收集相关信息以帮助研发部门制定产品路线图。\\n主要成绩：\\n帮助公司成功切入“边界扫描测试仪”市场；\\n作为主要贡献者赢得苹果公司$0.5M的订单。\",\"title\":\"技术营销工程师\",\"rangeDate\":\"2012-12至2014-7\"},{\"locationName\":\"Shanghai\",\"companyName\":\"Teradyne\",\"description\":\"Main responsibility:\\n1.Organize and coordinate internal and external resources to provide customers overall technical support, including installation, repair, maintenance, upgrade, calibration, etc.;\\n2.Monitor and manage representative service team to provide top class service(mean-time-to-respond, mean-time-to-repair, first-time-fix-rate, etc);\\n3.Manage parts center: analyze parts usage according to monthly report to find out how to improve parts stock level and turnover rate;\\n4.Manage repair center: analyze parts repair according to quarterly report to find out most parts-consuming customers and remind sales and service team to take special care; \\n5.Act as contact window between local customers, representative service team and R&D for all technology and non-technology issues;\\n6.Quarterly visit customers and review customer needs by conducting VOCs, summarize findings to senior management;\\n7.Analyze specific system/customer/agreement parts usage to provide data analysis for sales selling service contract;\\n8.Enact training plan to meet customer’s needs.\",\"title\":\"Senior Service Engineer\",\"rangeDate\":\"2004-7至2010-9\"}]', '[\"Debugging\",\"Testing\",\"Program Management\",\"Simulations\"]', 'jun-zhang-418b5a1a', '2017-05-17 16:13:46');
